import React, { useState } from 'react';
import '../style/recipt.css';
import axios from 'axios';

function FormComponent() {
  const [formData, setFormData] = useState({
    ingredients: '',
    steps: ''
  });

  const [imageFile, setImageFile] = useState(null); // for storing the selected image

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]); // Store the selected file
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('image', imageFile); // name should match multer config
    data.append('ingredients', formData.ingredients);
    data.append('steps', formData.steps);

    try {
      const response = await axios.post('http://localhost:5002/api/recipt', data, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.status === 200) {
        alert('Data inserted successfully');
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Data failed to submit');
    }
  };

  return (
    <form onSubmit={handleSubmit} name="form" encType="multipart/form-data">
      <h1 align="center" style={{ fontSize: '30px' }}>Recipe Sharing</h1>

      <div>
        <label>Image</label>
        <input type="file" name="image" onChange={handleFileChange} required />
      </div>

      <div>
        <label>Ingredients</label>
        <textarea  name="ingredients" value={formData.ingredients} onChange={handleChange} required placeholder="Enter ingredients" />
      </div>

      <div>
        <label>Steps</label>
        <input type="text" name="steps" value={formData.steps} onChange={handleChange} required placeholder="Enter steps" />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
}

export default FormComponent;
