import React, { useState, useEffect } from 'react';
import '../style/recipt.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Edit() {
  const [formData, setFormData] = useState({
    ingredients: '',
    steps: '',
    name: ''
  });

  const [imageFile, setImageFile] = useState(null); // for file upload
  const [imagePreview, setImagePreview] = useState(null); // for existing image

  const { id } = useParams();

  // Fetch existing data
  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5002/api/recipt/${id}`);
        const { name, ingredients, steps, imageUrl } = response.data;

        setFormData({ name, ingredients, steps });
        setImagePreview(`http://localhost:5002/${imageUrl}`); // existing image
      } catch (error) {
        console.error('Error fetching recipe:', error);
      }
    };

    fetchRecipe();
  }, [id]);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);

    // optional: show preview for selected image
    if (file) {
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    if (imageFile) data.append('image', imageFile);
    data.append('ingredients', formData.ingredients);
    data.append('steps', formData.steps);
    data.append('name', formData.name);

    try {
      const response = await axios.put(`http://localhost:5002/api/recipt/${id}`, data, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.status === 200) {
        alert('Recipe updated successfully');
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Failed to update recipe');
    }
  };

  return (
    <form onSubmit={handleSubmit} name="form" encType="multipart/form-data">
      <h1 align="center" style={{ fontSize: '30px' }}>Edit Recipe</h1>

      <div>
        <label>Recipe Name</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

      </div>

      <div>
        <label>Image</label>
        {imagePreview && (
          <img
            src={imagePreview}
            alt="Current"
            style={{ width: '200px', height: 'auto', marginBottom: '10px' }}
          />
        )}
        <input type="file" name="image" onChange={handleFileChange} />
      </div>

      <div>
        <label>Ingredients</label>
        <textarea
          name="ingredients"
          value={formData.ingredients}
          onChange={handleChange}

          required
          placeholder="Enter ingredients"
        />
      </div>

      <div>
        <label>Steps</label>
        <textarea
          name="steps"
          value={formData.steps}
          onChange={handleChange}
          required
          placeholder="Enter steps"
        />
      </div>

      <button type="submit">Update</button>
    </form>
  );
}

export default Edit;
