import React, { useEffect, useState } from 'react';
import '../style/Homepage.css';
const Homepage = () => {
  const [image, setImage] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5002/api/home")
      .then(response => response.json())
      .then(data => setImage(data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  return (
   <div>
     <input type="search" placeholder='Search via Name or ingridents'></input>

    <div className="Container">

      {image.map((images) => (
        <div key={images._id} className="Image">
          <img
            src={`http://localhost:5002/${images.imagePath}`}  alt="Uploaded"/>
            <div className="text-section">
            <h2>{images.name}</h2>
            <h3 >üßÇ Ingredients:</h3>
            <ul>
              {images.ingredients.split(/[,|\n]/).map((ing, idx) => (
                <li key={idx}>{ing.trim()}</li>
              ))}
            </ul>

            <h3 >üë®‚Äçüç≥ Steps:</h3>
            <ol>
              {images.steps.split(/[\n|.]/).filter(s => s.trim() !== "").map((step, idx) => (
                <li key={idx}>{step.trim()}</li>
              ))}
            </ol>
          </div>

        </div>
      ))}
    </div>
    </div>
  );
};

export default Homepage;
