import React, { useState } from 'react';
import '../style/recipt.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';

function Edit() {
  const [formData, setFormData] = useState({
    ingredients: '',
    steps: '',
    name:''
  });
  const [imageFile, setImageFile] = useState(null);
  const { id } = useParams();
  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5002/api/recipt/${id}`);
        const { name, ingredients, steps, imageUrl } = response.data;

        setFormData({ name, ingredients, steps });
        setImageFile(imageUrl); // store image URL for preview
      } catch (error) {
        console.error('Error fetching recipe:', error);
      }
    };

    fetchRecipe();
  }, [id]);



  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

//   const handleFileChange = (e) => {
//     setImageFile(e.target.files[0]); // Store the selected file
//   };

  const handleSubmit = async (e) => {
    e.preventDefault();


    const data = new FormData();
    data.append('image', imageFile); // name should match multer config
    data.append('ingredients', formData.ingredients);
    data.append('steps', formData.steps);
    data.append('name', formData.name);

    try {
        const response = await axios.put(`http://localhost:5002/api/recipt/${id}`, data, {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          });

      if (response.status === 200) {
        alert('Data inserted successfully');
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Data failed to submit');
    }
  };

  return (
    <form onSubmit={handleSubmit} name="form" encType="multipart/form-data">
      <h1 align="center" style={{ fontSize: '30px' }}>Recipe Sharing</h1>
      <div>
        <label>Recipe Name</label>
        <input type="text" name="name" onChange={handleChange} required />
      </div>

      <div>
        <label>Image</label>
        {/* <img src={`http://localhost:5002/${imageFile}`} alt="Current" style={{ width: '200px', height: 'auto', marginBottom: '10px' }} /> */}
      </div>

      <div>
        <label>Ingredients</label>
        <textarea  name="ingredients" value={formData.ingredients} onChange={handleChange} required placeholder="Enter ingredients" />
      </div>

      <div>
        <label>Steps</label>
        <textarea name="steps" value={formData.steps} onChange={handleChange} required placeholder="Enter steps" />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
}
export default Edit;
