import React, { useState } from 'react';
import '../style/recipt.css';
import axios from 'axios';
function FormComponent()
{
    const [FormData,SetFormData]=useState(
        {
            imagePath:'',
            ingredients:'',
            steps:'',


        });



    const handleChange=async(e)=>
    {
        const {name,value}=e.target;
        SetFormData({...FormData,[name]:value});

    } 


    const handleSubmit=async(e)=>
    {
        e.preventDefault();

    try
    {
        const response=await axios.post("http://localhost:5002/api/recipt",FormData);
        if(response.status===200)
        {
            alert("data inserted successfully");
            window.location.href="/";
        }
    }
    catch(error)
        {
            console.error("Error submitting form:", error);
            alert("Data failed to submit");
        }

    }

    return(
        <form onSubmit={handleSubmit} name="form">
            <h1 align="center" style={{fontSize:"30px"}}>Recipe Sharing</h1>
            <div>
            <label>Image</label>
            <input type="file" name="imagePath" value={FormData.imageUrl} onChange={handleChange} required placeholder="Enter your name"/>
            </div>

            <div>
            <label>ingredients</label>
            <input type="text" name="ingredients" value={FormData.ingredients} onChange={handleChange} required placeholder="Enter your email"/>
            </div>
            <div>
            <label>Steps</label>
            <input type="text" name="steps" value={FormData.steps} onChange={handleChange} required placeholder="Enter your number"/>
            </div>

            <button type="submit">Submit</button>

        </form>

    )

}
export default FormComponent;
