import React, { useState, useEffect } from 'react';
import SearchBar from './SearchForm';

const Homepage = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  // Fetch all recipes when the page loads
  useEffect(() => {
    fetch("http://localhost:5002/api/home")
      .then(response => response.json())
      .then(data => setRecipes(data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  // Function to handle search results
  const handleSearchResults = (data) => {
    setSearchResults(data);
  };

  const displayRecipes = searchResults.length > 0 ? searchResults : recipes;

  return (
    <div>
      <SearchBar onSearch={handleSearchResults} />
      <div className="Container">
        {displayRecipes.map((recipe) => (
          <div key={recipe._id} className="Image">
            <img
              src={`http://localhost:5002/${recipe.imagePath}`}
              alt="Recipe"
              style={{ width: '50%' }}
            />
            <p><b>Name:</b> {recipe.name}</p>
            <p><b>Ingredients:</b> {recipe.ingredients}</p>
            <p><b>Steps:</b> {recipe.steps} </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Homepage;
